/// –£—Å—Ç–∞–Ω–æ–≤–∏—Ç–µ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏:
// npm install react-router-dom styled-components motion

import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import styled, { createGlobalStyle } from 'styled-components';
import { motion, AnimatePresence } from 'motion/react';

// –ì–ª–æ–±–∞–ª—å–Ω—ã–µ —Å—Ç–∏–ª–∏ —Å –≥—Ä–∞–¥–∏–µ–Ω—Ç–æ–º –∏ —Å—Ç–µ–∫–ª—è–Ω–Ω—ã–º —ç—Ñ—Ñ–µ–∫—Ç–æ–º
const GlobalStyle = createGlobalStyle`
  body {
    margin: 0; padding: 0;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: #333333;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
`;

// –ö–æ–Ω—Ç–µ–π–Ω–µ—Ä —Å —ç—Ñ—Ñ–µ–∫—Ç–æ–º —Å—Ç–µ–∫–ª–∞ (glassmorphism)
const Container = styled.div`
  max-width: 720px;
  margin: 40px auto;
  padding: 30px 40px;
  background: rgba(255, 255, 255, 0.15);
  border-radius: 20px;
  box-shadow: 0 8px 32px rgba(31, 38, 135, 0.37);
  backdrop-filter: blur(12px);
  -webkit-backdrop-filter: blur(12px);
  border: 1px solid rgba(255, 255, 255, 0.18);
`;

// –ù–∞–≤–∏–≥–∞—Ü–∏—è —Å –ø–ª–∞–≤–Ω—ã–º —ç—Ñ—Ñ–µ–∫—Ç–æ–º
const Nav = styled.nav`
  display: flex;
  justify-content: space-around;
  margin-bottom: 30px;
  a {
    text-decoration: none;
    color: rgba(255, 255, 255, 0.7);
    font-weight: 700;
    font-size: 18px;
    padding-bottom: 6px;
    transition: all 0.3s ease;
    border-bottom: 3px solid transparent;

    &.active {
      color: #fff;
      border-bottom: 3px solid #ffd700;
      box-shadow: 0 2px 8px #ffd700aa;
      border-radius: 4px;
    }

    &:hover:not(.active) {
      color: #d1c4e9;
    }
